import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { auth, db } from '../utils/firebaseConfig'; // Assuming you have initialized Firestore in firebaseConfig
import { doc, getDoc } from 'firebase/firestore';

const Db = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async (userId) => {
      try {
        const webDevDocRef = doc(db, 'webDevelopment', userId);
        const appDevDocRef = doc(db, 'appDevelopment', userId);
        
        const webDevDoc = await getDoc(webDevDocRef);
        const appDevDoc = await getDoc(appDevDocRef);
        
        if (webDevDoc.exists()) {
          setUserData(webDevDoc.data());
        } else if (appDevDoc.exists()) {
          setUserData(appDevDoc.data());
        } else {
          setUserData(null);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        fetchUserData(user.uid);
      } else {
        navigate('/login', { replace: true });
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/login', { replace: true });
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Welcome to PulseZest Dashboard!</h1>
      {userData ? (
        <div>
          <h2>User Data:</h2>
          <pre>{JSON.stringify(userData, null, 2)}</pre>
        </div>
      ) : (
        <p>No user data found.</p>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Db;
