import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { collection, doc, getDoc } from 'firebase/firestore';
import { auth, db } from '../utils/firebaseConfig';

const Db = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/login', { replace: true });
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;

      if (user) {
        const userId = user.uid;

        try {
          // Fetch document from webDevelopment or appDevelopment collections based on userType
          const webDevDocRef = doc(collection(db, 'webDevelopment'), userId);
          const appDevDocRef = doc(collection(db, 'appDevelopment'), userId);
          const internDetailsDocRef = doc(collection(db, 'internDetails'), userId);
          const employeeDetailsDocRef = doc(collection(db, 'employeeDetails'), userId);

          const webDevDocSnap = await getDoc(webDevDocRef);
          const appDevDocSnap = await getDoc(appDevDocRef);
          const internDetailsSnap = await getDoc(internDetailsDocRef);
          const employeeDetailsSnap = await getDoc(employeeDetailsDocRef);

          // Check which document exists and setUserData accordingly
          if (webDevDocSnap.exists()) {
            setUserData(webDevDocSnap.data());
          } else if (appDevDocSnap.exists()) {
            setUserData(appDevDocSnap.data());
          } else if (internDetailsSnap.exists() && internDetailsSnap.data().userType === 'intern') {
            setUserData(internDetailsSnap.data());
          } else if (employeeDetailsSnap.exists() && employeeDetailsSnap.data().userType === 'employee') {
            setUserData(employeeDetailsSnap.data());
          } else {
            console.log('No matching documents.');
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        } finally {
          setLoading(false);
        }
      } else {
        navigate('/login', { replace: true });
      }
    };

    fetchUserData();
  }, [navigate]);

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
        <p>Loading...</p>
      </div>
    );
  }

  const handleLinkClick = (url) => {
    window.open(url, '_blank');
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px', backgroundColor: '#fff', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)', borderRadius: '8px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1 style={{ fontSize: '24px', fontWeight: 'bold' }}>Welcome to PulseZest Dashboard!</h1>
        <button
          style={{
            backgroundColor: '#f44336',
            color: '#fff',
            border: 'none',
            padding: '10px 20px',
            fontSize: '16px',
            cursor: 'pointer',
            borderRadius: '4px',
            transition: 'background-color 0.3s ease',
          }}
          onClick={handleLogout}
        >
          Logout
        </button>
      </header>
      <main style={{ padding: '20px', backgroundColor: '#f9f9f9', border: '1px solid #ddd', borderRadius: '4px' }}>
        <div style={{ marginBottom: '20px' }}>
          <h2 style={{ fontSize: '20px', marginBottom: '10px' }}>User Data: <span style={{ fontWeight: 'normal' }}>{userData?.userId}</span></h2>

          <div style={{ backgroundColor: '#fff', boxShadow: '0 0 5px rgba(0, 0, 0, 0.1)', padding: '20px', borderRadius: '4px' }}>
            <div style={{ marginBottom: '15px' }}>
              <strong>Full Name:</strong> {userData.fullName}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Email:</strong> {userData.email}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Phone Number:</strong> {userData.phoneNumber}
            </div>
            <div>
              {/* Clickable Website Name */}
              <div style={{ marginBottom: '15px', cursor: 'pointer' }} onClick={() => handleLinkClick(`http://${userData?.websiteName}`)}>
                <strong>Website Name:</strong> {userData?.websiteName}
              </div>

              {/* Clickable Domain */}
              <div style={{ marginBottom: '15px', cursor: 'pointer' }} onClick={() => handleLinkClick(`http://${userData?.domain}`)}>
                <strong>Domain:</strong> {userData?.domain}
              </div>

              {/* Clickable Custom Domain */}
              <div style={{ marginBottom: '15px', cursor: 'pointer' }} onClick={() => handleLinkClick(`http://${userData?.customDomain}`)}>
                <strong>Custom Domain:</strong> {userData?.customDomain}
              </div>
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Host:</strong> {userData.host}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Description:</strong> {userData.description}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Purpose:</strong> {userData.purpose}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Reference:</strong> {userData.reference}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Registration Date:</strong> {new Date(userData.registrationDate).toLocaleDateString()}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <strong>Project Live:</strong> {userData.projectLive ? 'Yes' : 'No'}
            </div>
            {userData.projectLiveDate && (
              <div style={{ marginBottom: '15px' }}>
                <strong>Project Live Date:</strong> {new Date(userData.projectLiveDate).toLocaleDateString()}
              </div>
            )}
            {userData.renewDate && (
              <div style={{ marginBottom: '15px' }}>
                <strong>Renew Date:</strong> {new Date(userData.renewDate).toLocaleDateString()}
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Db;
