import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { collection, doc, getDoc } from 'firebase/firestore';
import { auth, db } from '../utils/firebaseConfig';

// Import your components for each dashboard
import EmployeeDashboard from './EmployeeDashboard';
import InternDashboard from './DashBoards/InternDashboard';
import AppDevDashboard from './DashBoards/AppDevDashboard';
import WebDevDashboard from './DashBoards/WebDevDashboard';

const Db = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/login', { replace: true });
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;

      if (user) {
        const userId = user.uid;

        try {
          // Check which collection to fetch based on user role or type
          let collectionName;
          switch (user.role) { // Assuming role is stored in user object after login
            case 'employee':
              collectionName = 'employeeDetails';
              break;
            case 'intern':
              collectionName = 'internDetails';
              break;
            case 'appDev':
              collectionName = 'appDevelopment';
              break;
            case 'webDev':
              collectionName = 'webDevelopment';
              break;
            default:
              console.error('Invalid user role:', user.role);
              navigate('/login', { replace: true });
              return;
          }

          const userDocRef = doc(collection(db, collectionName), userId);
          const userDocSnap = await getDoc(userDocRef);

          if (userDocSnap.exists()) {
            setUserData(userDocSnap.data());
          } else {
            console.log(`No matching document for ${user.role}.`);
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        } finally {
          setLoading(false);
        }
      } else {
        navigate('/login', { replace: true });
      }
    };

    fetchUserData();
  }, [navigate]);

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh' }}>
        <p>Loading...</p>
      </div>
    );
  }

  if (!userData) {
    return (
      <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px', backgroundColor: '#fff', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)', borderRadius: '8px' }}>
        <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h1 style={{ fontSize: '24px', fontWeight: 'bold' }}>Welcome to PulseZest Dashboard!</h1>
          <button
            style={{
              backgroundColor: '#f44336',
              color: '#fff',
              border: 'none',
              padding: '10px 20px',
              fontSize: '16px',
              cursor: 'pointer',
              borderRadius: '4px',
              transition: 'background-color 0.3s ease',
            }}
            onClick={handleLogout}
          >
            Logout
          </button>
        </header>
        <main style={{ padding: '20px', backgroundColor: '#f9f9f9', border: '1px solid #ddd', borderRadius: '4px' }}>
          <div style={{ marginBottom: '20px' }}>
            <h2 style={{ fontSize: '20px', marginBottom: '10px' }}>User Data:</h2>
            <p>No user data found.</p>
          </div>
        </main>
      </div>
    );
  }

  // Render the appropriate dashboard based on user role
  switch (userData.role) {
    case 'employee':
      return <EmployeeDashboard userData={userData} handleLogout={handleLogout} />;
    case 'intern':
      return <InternDashboard userData={userData} handleLogout={handleLogout} />;
    case 'appDev':
      return <AppDevDashboard userData={userData} handleLogout={handleLogout} />;
    case 'webDev':
      return <WebDevDashboard userData={userData} handleLogout={handleLogout} />;
    default:
      console.error('Invalid user role:', userData.role);
      return null;
  }
};

export default Db;
