import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { collection, doc, getDoc } from 'firebase/firestore';
import { auth, db } from '../utils/firebaseConfig';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Container,
  Card,
  CardContent,
  CircularProgress,
  Box,
} from '@mui/material';

const Db = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/login', { replace: true });
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;

      if (user) {
        const userId = user.uid;

        try {
          const webDevDocRef = doc(collection(db, 'webDevelopment'), userId);
          const appDevDocRef = doc(collection(db, 'appDevelopment'), userId);

          const webDevDocSnap = await getDoc(webDevDocRef);
          const appDevDocSnap = await getDoc(appDevDocRef);

          if (webDevDocSnap.exists()) {
            setUserData(webDevDocSnap.data());
          } else if (appDevDocSnap.exists()) {
            setUserData(appDevDocSnap.data());
          } else {
            console.log('No matching documents.');
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        } finally {
          setLoading(false);
        }
      } else {
        navigate('/login', { replace: true });
      }
    };

    fetchUserData();
  }, [navigate]);

  if (loading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <div>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            PulseZest Dashboard
          </Typography>
          <Button color="inherit" onClick={handleLogout}>
            Logout
          </Button>
        </Toolbar>
      </AppBar>
      <Container sx={{ mt: 4 }}>
        <Card>
          <CardContent>
            <Typography variant="h5" component="div">
              User Data
            </Typography>
            {userData ? (
              <Box sx={{ mt: 2 }}>
                <pre>{JSON.stringify(userData, null, 2)}</pre>
              </Box>
            ) : (
              <Typography sx={{ mt: 2 }} color="text.secondary">
                No user data found.
              </Typography>
            )}
          </CardContent>
        </Card>
      </Container>
    </div>
  );
};

export default Db;
