import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { collection, doc, getDoc } from 'firebase/firestore';
import { auth, db } from '../utils/firebaseConfig';  // Adjust the import to include Firestore (db)

const Db = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/login', { replace: true });
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  useEffect(() => {
    const fetchUserData = async () => {
      const user = auth.currentUser;

      if (user) {
        const userId = user.uid;

        try {
          const webDevDocRef = doc(collection(db, 'webDevelopment'), userId);
          const appDevDocRef = doc(collection(db, 'appDevelopment'), userId);

          const webDevDocSnap = await getDoc(webDevDocRef);
          const appDevDocSnap = await getDoc(appDevDocRef);

          if (webDevDocSnap.exists()) {
            setUserData(webDevDocSnap.data());
          } else if (appDevDocSnap.exists()) {
            setUserData(appDevDocSnap.data());
          } else {
            console.log('No matching documents.');
          }
        } catch (error) {
          console.error('Error fetching user data:', error);
        } finally {
          setLoading(false);
        }
      } else {
        navigate('/login', { replace: true });
      }
    };

    fetchUserData();
  }, [navigate]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Welcome to PulseZest Dashboard!</h1>
      {userData ? (
        <div>
          <h2>User Data:</h2>
          <pre>{JSON.stringify(userData, null, 2)}</pre>
        </div>
      ) : (
        <div>No user data found.</div>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Db;
